import React, { Component } from 'react';

import './App.css';

class App extends Component {

  state={
    word:"",
  }

  onWordChange = (e) =>{
    this.setState({
      ...this.state,
      word:e.target.value
    })
  }

  render() {
    return(
      <div >
        <label htmlFor="word-search">Word</label>
        <input type="text" value={this.props.word} name="word" id="word-search" onChange={this.onWordChange}/>
      </div>
    )
  }
}




export default App;
