import React, { Component } from 'react';
import axios from "axios";
import './App.css';
import Autosuggest from 'react-autosuggest';

const renderSuggestion = suggestion => (
  <div>
    {suggestion.name}
  </div>
);
class App extends Component {

  state={
    word:"",
    resPonse:[],
  }

  onSuggestionsFetchRequested = ({ word }) => {
    this.setState({
      suggestions: this.getWords(word)
    });
  };

  onSuggestionsClearRequested = () => {
    this.setState({
      suggestions: []
    });
  };
  getSuggestionValue = (suggestion) => {
    return suggestion.word
  }
  getWords = (word) =>{
    axios({
      method: 'GET',
      url: "http://127.0.0.1:8000/api/?search="+word, //Development
      headers: {
        "Content-Type": 'application/json',
        'Accept': 'application/json',
      },
    }).then(res=>{
      if (res.status===200){
        console.log(res)
        this.setState({
          ...this.state,
          resPonse:res.data,
        })
      }
    }).catch(err=>{
      console.error(err.response)
    })
  }

  onWordChange = (e, { newValue }) =>{
    this.setState({
      ...this.state,
      word:newValue
    })

  }


  render() {
    const inputProps = {
      placeholder: 'Type a programming language',
      word:this.state.word,
      onChange: this.onWordChange
    };
    return(
      <div >
        <label htmlFor="word-search">Word</label>
        <input type="text" value={this.props.word} name="word" id="word-search" onChange={this.onWordChange}/>
        <Autosuggest
        suggestions={this.state.resPonse}
        onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
        onSuggestionsClearRequested={this.onSuggestionsClearRequested}
        inputProps={inputProps}
        renderSuggestion={renderSuggestion}
        getSuggestionValue={this.getSuggestionValue}
      />
      </div>
    )
  }
}




export default App;
