import React, { Component } from 'react';
import axios from "axios";
import './App.css';
import Autosuggest from 'react-autosuggest';
class App extends Component {

  state={
    word:"",
    resPonse:["a", "b"],
  }

  // onSuggestionsFetchRequested = ({ value }) => {
  //   this.setState({
  //     suggestions: getSuggestions(value)
  //   });
  // };

  // onSuggestionsClearRequested = () => {
  //   this.setState({
  //     suggestions: []
  //   });
  // };

  getWords = (word) =>{
    axios({
      method: 'GET',
      url: "http://127.0.0.1:8000/api/?search="+word, //Development
      headers: {
        "Content-Type": 'application/json',
        'Accept': 'application/json',
      },
    }).then(res=>{
      if (res.status===200){
        console.log(res)
        this.setState({
          ...this.state,
          resPonse:res.data,
        })
      }
    }).catch(err=>{
      console.error(err.response)
    })
  }

  onWordChange = (e) =>{
    this.setState({
      ...this.state,
      word:e.target.value
    })
    this.getWords(e.target.value)

  }

  render() {
    const inputProps = {
      placeholder: 'Type a programming language',
      value,
      onChange: this.onChange
    };
    return(
      <div >
        <label htmlFor="word-search">Word</label>
        <input type="text" value={this.props.word} name="word" id="word-search" onChange={this.onWordChange}/>
        <Autosuggest
        suggestions={this.state.resPonse}
      />
      </div>
    )
  }
}




export default App;
